import React, { useEffect, useState } from 'react';
import { 
    Dialog, 
    DialogTitle, 
    DialogContent, 
    DialogActions, 
    Button, 
    Typography, 
    Box,
    List,
    ListItem,
    ListItemText,
    CircularProgress
} from '@mui/material';
import socketIOClient from 'socket.io-client';

const TestConnection = ({ open, onClose }) => {
    const [apiStatus, setApiStatus] = useState(null);
    const [socketStatus, setSocketStatus] = useState('disconnected');
    const [messages, setMessages] = useState([]);
    const [socket, setSocket] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Test HTTP endpoint
        const testApiConnection = async () => {
            try {
                setIsLoading(true);
                const response = await fetch('http://localhost:5000/api/test_connection');
                const data = await response.json();
                setApiStatus(data);
            } catch (error) {
                setApiStatus({ status: 'error', message: error.message });
            } finally {
                setIsLoading(false);
            }
        };

        // Initialize Socket.IO connection
        const socket = socketIOClient('http://localhost:5000');
        setSocket(socket);

        socket.on('connect', () => {
            setSocketStatus('connected');
            console.log('Socket.IO connected');
        });

        socket.on('disconnect', () => {
            setSocketStatus('disconnected');
            console.log('Socket.IO disconnected');
        });

        socket.on('test_response', (data) => {
            console.log('Received test response:', data);
            setMessages(prev => [...prev, data]);
        });

        testApiConnection();

        return () => {
            socket.disconnect();
        };
    }, []);

    const sendTestMessage = () => {
        if (socket) {
            socket.emit('test_message', {
                session_id: 'test_session',
                message: 'Hello from client!'
            });
        }
    };

    return (
        <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
            <DialogTitle>Connection Test</DialogTitle>
            <DialogContent>
                <Box sx={{ mb: 3 }}>
                    <Typography variant="h6" gutterBottom>HTTP API Status:</Typography>
                    {isLoading ? (
                        <CircularProgress size={24} />
                    ) : (
                        <pre style={{ 
                            backgroundColor: '#f5f5f5', 
                            padding: '10px', 
                            borderRadius: '4px',
                            overflow: 'auto'
                        }}>
                            {JSON.stringify(apiStatus, null, 2)}
                        </pre>
                    )}
                </Box>

                <Box sx={{ mb: 3 }}>
                    <Typography variant="h6" gutterBottom>Socket.IO Status: {socketStatus}</Typography>
                    <Button 
                        variant="contained" 
                        onClick={sendTestMessage} 
                        disabled={socketStatus !== 'connected'}
                        sx={{ mt: 1 }}
                    >
                        Send Test Message
                    </Button>
                </Box>

                <Box>
                    <Typography variant="h6" gutterBottom>Received Messages:</Typography>
                    <List>
                        {messages.map((msg, index) => (
                            <ListItem key={index}>
                                <ListItemText
                                    primary={
                                        <pre style={{ 
                                            backgroundColor: '#f5f5f5', 
                                            padding: '10px', 
                                            borderRadius: '4px',
                                            overflow: 'auto'
                                        }}>
                                            {JSON.stringify(msg, null, 2)}
                                        </pre>
                                    }
                                />
                            </ListItem>
                        ))}
                    </List>
                </Box>
            </DialogContent>
            <DialogActions>
                <Button onClick={onClose}>Close</Button>
            </DialogActions>
        </Dialog>
    );
};

export default TestConnection;
